// Pega a lista de notícias que veio do nó HTTP Request
const noticias = $json.rss.channel.item;

// Limita a lista para apenas as 5 primeiras
const top5 = noticias.slice(0, 5);

let mensagemFormatada = "Aqui estão as 5 principais notícias do G1:\n\n";

// Loop para criar a mensagem
for (const noticia of top5) {
  // Adiciona o título e o link de cada notícia
  mensagemFormatada += `• ${noticia.title} - ${noticia.link}\n\n`;
}

// Retorna a mensagem formatada para o próximo nó
return {
  mensagemFinal: mensagemFormatada
};
